import { Icon } from '@iconify/react/dist/iconify.js';
import React, { useEffect, useState, useRef } from 'react';
import { getMenuListByRestaurantIdAndRestaurantName, getRestaurantColorTheme } from '../Services/allApi';
import AddCart from './AddCart';
import { useSearchParams } from 'react-router';

function Home() {
  const [items, setItems] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('Starters');
  const [selectedType, setSelectedType] = useState('Veg');
  const [loading, setLoading] = useState(true);
  const [cartItems, setCartItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchParams] = useSearchParams();

  const restaurantId = searchParams.get('restaurantId');
  const restaurantName = searchParams.get('restaurantName') || 'Italian Dorado';

  const [themeColor, setThemeColor] = useState(() => {
    const cachedTheme = localStorage.getItem('themeColor');
    return cachedTheme ? cachedTheme : "#2ebf6c";
  });

  const searchInputRef = useRef(null);
  const [showDropdown, setShowDropdown] = useState(false);
  const dropdownRef = useRef(null);

  // Fixed categories
  const categories = ['Starters', 'Main Course', 'Desserts'];

  useEffect(() => {
    const fetchColorTheme = async () => {
      const headers = { 'Authorization': 'Basic ' + btoa('admin:admin123') };
      try {
        const res = await getRestaurantColorTheme(restaurantId, headers);
        const newTheme = res?.data || {};
        const finalColor = newTheme.backgroundColor || "#2ebf6c";
        setThemeColor(finalColor);
        localStorage.setItem('themeColor', finalColor);
      } catch (error) {
        console.error('Error fetching restaurant color theme:', error);
      }
    };

    fetchColorTheme();
  }, [restaurantId]);

  const scrollToSearchBar = () => {
    if (searchInputRef.current) {
      searchInputRef.current.scrollIntoView({ behavior: 'smooth' });
      searchInputRef.current.focus();
    }
  };

  const updateToCart = (id) => {
    setItems(items.map(item =>
      item.id === id ? { ...item, cartCount: 1 } : item
    ));
    const selectedItem = items.find(item => item.id === id);
    if (selectedItem) {
      setCartItems([...cartItems, { ...selectedItem, cartCount: 1 }]);
    }
  };

  const plusItems = (id) => {
    setItems(items.map(item =>
      item.id === id ? { ...item, cartCount: item.cartCount + 1 } : item
    ));
    setCartItems(cartItems.map(item =>
      item.id === id ? { ...item, cartCount: item.cartCount + 1 } : item
    ));
  };

  const minusItems = (id) => {
    setItems(items.map(item =>
      item.id === id && item.cartCount > 0
        ? { ...item, cartCount: item.cartCount - 1 } : item
    ));
    setCartItems(cartItems
      .map(item => item.id === id ? { ...item, cartCount: item.cartCount - 1 } : item)
      .filter(item => item.cartCount > 0)
    );
  };

  const fetchItems = async () => {
    const headers = { 'Authorization': 'Basic ' + btoa('admin:admin123') };
    try {
      const res = await getMenuListByRestaurantIdAndRestaurantName(restaurantId, restaurantName, headers);
      const newData = res?.data?.map((item) => ({
        id: item?.id,
        name: item?.itemName,
        description: item?.itemDescription || "",
        price: item?.price,
        imageUrl: item?.imageUrl ?? 'https://developers.elementor.com/docs/assets/img/elementor-placeholder-image.png',
        category: item?.category ?? 'Starters',
        cartCount: 0,
        type: item?.type ?? 'Veg',
        rating: item?.rating ?? 4.2,
        ratingCount: item?.ratingCount ?? 250,
      }));
      setItems(newData);
    } catch (error) {
      console.error('Error fetching menu items:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchItems();
  }, [restaurantId, restaurantName]);

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
    setShowDropdown(false);
  };

  const handleTypeChange = (type) => {
    setSelectedType(type);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredItems = items.filter(item =>
    (selectedCategory === '' || item.category === selectedCategory) &&
    (selectedType === 'All' || item.type === selectedType) &&
    (item.name.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  return (
    <div className="bg-white min-h-screen text-gray-800">
      {/* Header */}
      <div className="px-4 py-6 border-b border-gray-200">
        <h1 className="text-xl font-bold text-gray-800">Find delicious items from</h1>
        <h2 className="text-2xl font-bold text-green-600 mt-1">{restaurantName}</h2>
      </div>

      {/* Search */}
      <div className="p-3 border-b">
        <input
          ref={searchInputRef}
          className="w-full px-3 py-2 border rounded-md focus:outline-none"
          placeholder="Search for dishes"
          value={searchTerm}
          onChange={handleSearchChange}
        />
      </div>

      {/* Filters */}
      <div className="flex items-center justify-between p-3 border-b">
        <button onClick={() => handleTypeChange(selectedType === 'Veg' ? 'All' : 'Veg')} className="flex items-center">
          <span className={`w-5 h-5 rounded-full ${selectedType === 'Veg' ? 'bg-green-500' : 'bg-gray-300'}`} />
          <span className="ml-2">Veg</span>
        </button>
        <button onClick={() => handleTypeChange(selectedType === 'Non-Veg' ? 'All' : 'Non-Veg')} className="flex items-center">
          <span className={`w-5 h-5 rounded-full ${selectedType === 'Non-Veg' ? 'bg-red-500' : 'bg-gray-300'}`} />
          <span className="ml-2">Non-Veg</span>
        </button>
      </div>

      {/* Item Listing */}
      <div className="pb-16">
        {loading ? (
          <p>Loading...</p>
        ) : (
          filteredItems.map((item) => (
            <div key={item.id} className="flex items-center p-4 border-b">
              <img
                src={item.imageUrl}
                alt={item.name}
                className="w-40 h-40 rounded-md object-cover"
              />
              <div className="ml-4 flex-1">
                <h3 className="text-lg font-semibold">{item.name}</h3>
                <p className="text-gray-600 text-sm my-2">{item.description}</p>
                <span className="font-bold text-gray-800">₹ {item.price}</span>
              </div>
              <div className="flex flex-col items-center">
                {item.cartCount === 0 ? (
                  <button onClick={() => updateToCart(item.id)} className="bg-green-500 text-white px-3 py-1 rounded-full">
                    ADD
                  </button>
                ) : (
                  <div className="flex items-center">
                    <button onClick={() => minusItems(item.id)} className="text-green-500 text-xl">-</button>
                    <span className="mx-2">{item.cartCount}</span>
                    <button onClick={() => plusItems(item.id)} className="text-green-500 text-xl">+</button>
                  </div>
                )}
              </div>
            </div>
          ))
        )}
      </div>

      {/* Cart */}
      <AddCart
        themeColor={themeColor}
        cartCount={cartItems.length}
        cartItems={cartItems}
        onSearchIconClick={scrollToSearchBar}
      />
    </div>
  );
}

export default Home;
