import React, { useState } from 'react';
import { Icon } from '@iconify/react';

function AddCart() {
  const [activeTab, setActiveTab] = useState("home");

  // Set the active tab based on the clicked icon
  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="fixed bottom-0 w-full h-20 bg-white drop-shadow-2xl shadow-2xl z-50 flex items-center justify-around px-4 rounded-t-lg border-t border-gray-200">
      {/* Home, Search, Profile, and Cart Icons */}
      <div className="flex justify-between w-full max-w-lg items-center">
        
        {/* Home Icon */}
        <div
          onClick={() => handleTabChange("home")}
          className={`flex flex-col items-center cursor-pointer p-3 transition-all duration-300 ease-in-out rounded-full ${
            activeTab === "home" ? "bg-[#FF5A00] text-white" : "bg-gray-200 text-gray-700"
          } hover:bg-[#FF5A00] hover:text-white`}
        >
          <Icon icon="mdi:home" className="text-3xl transition-all duration-300" />
          <span className="text-sm mt-1">Home</span>
        </div>

        {/* Search Icon */}
        <div
          onClick={() => handleTabChange("search")}
          className={`flex flex-col items-center cursor-pointer p-3 transition-all duration-300 ease-in-out rounded-full ${
            activeTab === "search" ? "bg-[#FF5A00] text-white" : "bg-gray-200 text-gray-700"
          } hover:bg-[#FF5A00] hover:text-white`}
        >
          <Icon icon="mdi:magnify" className="text-3xl transition-all duration-300" />
          <span className="text-sm mt-1">Search</span>
        </div>

        {/* Profile Icon */}
        <div
          onClick={() => handleTabChange("profile")}
          className={`flex flex-col items-center cursor-pointer p-3 transition-all duration-300 ease-in-out rounded-full ${
            activeTab === "profile" ? "bg-[#FF5A00] text-white" : "bg-gray-200 text-gray-700"
          } hover:bg-[#FF5A00] hover:text-white`}
        >
          <Icon icon="mdi:account" className="text-3xl transition-all duration-300" />
          <span className="text-sm mt-1">Profile</span>
        </div>

        {/* Cart Icon */}
        <div
          onClick={() => handleTabChange("cart")}
          className={`flex flex-col items-center cursor-pointer p-3 transition-all duration-300 ease-in-out rounded-full ${
            activeTab === "cart" ? "bg-[#FF5A00] text-white" : "bg-gray-200 text-gray-700"
          } hover:bg-[#FF5A00] hover:text-white`}
        >
          <Icon
            icon="fluent:cart-24-regular"
            className="text-3xl transition-all duration-300"
          />
          <span className="text-sm mt-1">Cart</span>
        </div>
      </div>
    </div>
  );
}

export default AddCart;
