import { Icon } from '@iconify/react/dist/iconify.js';
import React, { useEffect, useState, useRef, useCallback } from 'react';
import { getMenuListByRestaurantIdAndRestaurantName, getRestaurantColorTheme } from '../Services/allApi';
import AddCart from './AddCart';
import { useSearchParams } from 'react-router';

function Home() {
  const [items, setItems] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('Starters');
  const [selectedType, setSelectedType] = useState('Veg');
  const [loading, setLoading] = useState(true);
  const [cartItems, setCartItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [themeColor, setThemeColor] = useState("#2ebf6c");
  const [showDropdown, setShowDropdown] = useState(false);

  const searchInputRef = useRef(null);
  const dropdownRef = useRef(null);

  const [searchParams] = useSearchParams();
  const restaurantId = searchParams.get('restaurantId');
  const restaurantName = searchParams.get('restaurantName') || 'Italian Dorado';

  const categories = ['Starters', 'Main Course', 'Desserts'];

  // Fetch color theme and items
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const headers = { 'Authorization': 'Basic ' + btoa('admin:admin123') };
        
        const themeRes = await getRestaurantColorTheme(restaurantId, headers);
        setThemeColor(themeRes?.data?.backgroundColor || "#2ebf6c");

        const menuRes = await getMenuListByRestaurantIdAndRestaurantName(restaurantId, restaurantName, headers);
        const formattedItems = menuRes?.data?.map((item) => ({
          id: item?.id,
          name: item?.itemName,
          description: item?.itemDescription || "",
          price: item?.price,
          imageUrl: item?.imageUrl || 'https://via.placeholder.com/150',
          category: item?.category || 'Starters',
          cartCount: 0,
          type: item?.type || 'Veg',
          rating: item?.rating || 4.2,
          ratingCount: item?.ratingCount || 250,
          customisable: item?.customisable || false,
        }));

        setItems(formattedItems || []);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [restaurantId, restaurantName]);

  const handleSearchChange = (e) => setSearchTerm(e.target.value);
  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
    setShowDropdown(false);
  };

  const updateCart = (id, action) => {
    setItems((prevItems) =>
      prevItems.map((item) => {
        if (item.id === id) {
          const newCount =
            action === 'add' ? item.cartCount + 1 : Math.max(0, item.cartCount - 1);
          return { ...item, cartCount: newCount };
        }
        return item;
      })
    );
  };

  const filteredItems = items.filter((item) =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
    (selectedCategory === 'All' || item.category === selectedCategory) &&
    (selectedType === 'All' || item.type === selectedType)
  );

  const SkeletonLoader = React.memo(() => (
    <div className="w-full flex items-start p-4 border-b border-gray-200 animate-pulse">
      <div className="flex-1 space-y-3">
        <div className="h-6 bg-gray-200 rounded w-2/3"></div>
        <div className="h-5 bg-gray-200 rounded w-3/4"></div>
        <div className="h-4 bg-gray-200 rounded w-1/2"></div>
      </div>
      <div className="w-32 h-32 ml-6 bg-gray-200 rounded-lg"></div>
    </div>
  ));

  return (
    <div className="bg-white min-h-screen font-sans text-gray-800">
      {/* Header */}
      <div className="px-4 py-6 border-b border-gray-200">
        <h1 className="font-bold text-xl text-gray-900">Find delicious items from</h1>
        <h2 className="font-bold text-2xl mt-1 text-green-600">{restaurantName}</h2>
      </div>

      {/* Search Bar */}
      <div className="sticky top-0 z-10 p-4 bg-white border-b">
        <div className="relative">
          <input
            ref={searchInputRef}
            className="w-full bg-gray-100 px-5 py-3 rounded-full text-gray-700 placeholder-gray-500 focus:ring-2 focus:ring-green-500 focus:outline-none"
            type="text"
            value={searchTerm}
            onChange={handleSearchChange}
            placeholder="Search for dishes..."
          />
          <Icon
            icon="ic:round-search"
            className="absolute right-5 top-1/2 transform -translate-y-1/2 text-gray-500 text-xl"
          />
        </div>
      </div>

      {/* Category Filters */}
      <div className="p-4 flex justify-between items-center">
        <div>
          <button
            onClick={() => setSelectedType('Veg')}
            className={`mr-2 px-3 py-1 rounded-full text-sm ${
              selectedType === 'Veg' ? 'bg-green-600 text-white' : 'bg-gray-200'
            }`}
          >
            Veg
          </button>
          <button
            onClick={() => setSelectedType('Non-Veg')}
            className={`px-3 py-1 rounded-full text-sm ${
              selectedType === 'Non-Veg' ? 'bg-red-600 text-white' : 'bg-gray-200'
            }`}
          >
            Non-Veg
          </button>
        </div>
        <div className="relative" ref={dropdownRef}>
          <button
            onClick={() => setShowDropdown(!showDropdown)}
            className="px-3 py-1 border border-gray-300 rounded-full bg-white"
          >
            {selectedCategory} <Icon icon="ic:round-arrow-drop-down" />
          </button>
          {showDropdown && (
            <div className="absolute right-0 mt-2 w-40 bg-white border rounded shadow">
              {categories.map((cat) => (
                <div
                  key={cat}
                  onClick={() => handleCategoryChange(cat)}
                  className="p-2 hover:bg-gray-100 cursor-pointer"
                >
                  {cat}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Item List */}
      <div>
        {loading
          ? Array(5)
              .fill()
              .map((_, idx) => <SkeletonLoader key={idx} />)
          : filteredItems.map((item) => (
              <div key={item.id} className="p-4 border-b flex items-start">
                <div className="flex-1">
                  <h3 className="font-semibold text-lg">{item.name}</h3>
                  <p className="text-sm text-gray-600">{item.description}</p>
                  <p className="font-bold text-gray-900 mt-2">₹{item.price}</p>
                </div>
                <div className="relative w-32 h-32 ml-4">
                  <img
                    src={item.imageUrl}
                    alt={item.name}
                    className="w-full h-full object-cover rounded-lg"
                  />
                  <button
                    onClick={() => updateCart(item.id, 'add')}
                    className="absolute bottom-0 left-1/2 transform -translate-x-1/2 bg-green-500 text-white px-4 py-2 rounded-full"
                  >
                    ADD
                  </button>
                </div>
              </div>
            ))}
      </div>

      {/* Cart */}
      <AddCart themeColor={themeColor} cartCount={cartItems.length} cartItems={cartItems} />
    </div>
  );
}

export default Home;
