import { Icon } from '@iconify/react/dist/iconify.js';
import React, { useEffect, useState, useRef } from 'react';
import { getMenuListByRestaurantIdAndRestaurantName, getRestaurantColorTheme } from '../Services/allApi';
import AddCart from './AddCart';
import { useSearchParams } from 'react-router';
import debounce from 'lodash.debounce';

function Home() {
  const [items, setItems] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('Starters');
  const [selectedType, setSelectedType] = useState('Veg');
  const [loading, setLoading] = useState(true);
  const [cartItems, setCartItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [themeColor, setThemeColor] = useState(localStorage.getItem('themeColor') || '#2ebf6c');

  const [searchParams] = useSearchParams();
  const searchInputRef = useRef(null);
  const dropdownRef = useRef(null);
  const [showDropdown, setShowDropdown] = useState(false);

  const restaurantId = searchParams.get('restaurantId');
  const restaurantName = searchParams.get('restaurantName') || 'Italian Dorado';

  const categories = ['Starters', 'Main Course', 'Desserts'];

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const headers = getHeaders();
      await fetchColorTheme(headers);
      await getItems(headers);
      setLoading(false);
    };
    fetchData();
    window.scrollTo(0, 0);
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
    };
    const debouncedScroll = debounce(() => setShowDropdown(false), 100);

    document.addEventListener('mousedown', handleClickOutside);
    window.addEventListener('scroll', debouncedScroll);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      window.removeEventListener('scroll', debouncedScroll);
    };
  }, []);

  const getHeaders = () => ({
    Authorization: 'Basic ' + btoa('admin:admin123'),
  });

  const fetchColorTheme = async (headers) => {
    try {
      const res = await getRestaurantColorTheme(restaurantId, headers);
      const finalColor = res?.data?.backgroundColor || '#2ebf6c';
      setThemeColor(finalColor);
      localStorage.setItem('themeColor', finalColor);
    } catch (error) {
      console.error('Error fetching restaurant color theme:', error);
    }
  };

  const getItems = async (headers) => {
    try {
      const res = await getMenuListByRestaurantIdAndRestaurantName(restaurantId, restaurantName, headers);
      const newData = res?.data?.map((item) => ({
        id: item?.id,
        name: item?.itemName,
        description: item?.itemDescription || '',
        price: item?.price,
        imageUrl: item?.imageUrl ?? 'https://developers.elementor.com/docs/assets/img/elementor-placeholder-image.png',
        category: item?.category ?? 'Starters',
        cartCount: 0,
        type: item?.type ?? 'Veg',
        rating: item?.rating ?? 4.2,
        ratingCount: item?.ratingCount ?? 250,
        customisable: item?.customisable || false,
      }));
      setItems(newData);
    } catch (error) {
      alert('Failed to fetch menu items');
    }
  };

  const updateCart = (id, action) => {
    setItems(items.map((item) =>
      item.id === id
        ? { ...item, cartCount: Math.max(0, action === 'add' ? item.cartCount + 1 : item.cartCount - 1) }
        : item
    ));
    const selectedItem = items.find(item => item.id === id);
    if (selectedItem) {
      setCartItems(prevCart => {
        const exists = prevCart.find(ci => ci.id === id);
        if (!exists && action === 'add') return [...prevCart, { ...selectedItem, cartCount: 1 }];
        return prevCart.filter(ci => ci.id !== id || action === 'remove');
      });
    }
  };

  const filteredItems = items.filter((item) =>
    (selectedCategory === '' || item.category === selectedCategory) &&
    (selectedType === 'All' || item.type === selectedType) &&
    (item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      item.description.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const MenuItem = React.memo(({ item }) => (
    <div className="w-full flex flex-row items-start p-4 border-b border-gray-200">
      <div className="flex-1 pr-4">
        <h1 className="font-semibold text-lg">{item.name}</h1>
        <p>â‚¹ {item.price}</p>
        <p className="text-sm text-gray-600">{item.description}</p>
      </div>
      <div className="relative w-36 h-36">
        <img src={item.imageUrl} className="w-full h-full rounded" alt={item.name} />
        <button
          onClick={() => updateCart(item.id, 'add')}
          className="absolute bottom-0 bg-green-500 text-white px-4 py-1 rounded"
        >
          {item.cartCount > 0 ? `Count: ${item.cartCount}` : 'ADD'}
        </button>
      </div>
    </div>
  ));

  return (
    <div className="bg-white min-h-screen">
      <header className="p-4 border-b">
        <h1 className="text-2xl font-bold text-green-600">{restaurantName}</h1>
        <input
          ref={searchInputRef}
          placeholder="Search for dishes..."
          className="w-full p-2 border rounded mt-2"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </header>

      <div className="flex p-4 gap-4">
        {categories.map((category) => (
          <button
            key={category}
            onClick={() => setSelectedCategory(category)}
            className={`px-3 py-1 border rounded ${selectedCategory === category ? 'bg-green-500 text-white' : ''}`}
          >
            {category}
          </button>
        ))}
      </div>

      {loading ? (
        <div>Loading...</div>
      ) : (
        filteredItems.map((item) => <MenuItem key={item.id} item={item} />)
      )}

      <AddCart cartItems={cartItems} themeColor={themeColor} />
    </div>
  );
}

export default Home;
