import React from 'react';
import { Icon } from '@iconify/react'; // Import Iconify's Icon component

function AddCart() {
  return (
    <div className="fixed bottom-0 w-full h-20 bg-gray-50 drop-shadow-2xl shadow-2xl z-50 flex items-center justify-around px-4 rounded-t-lg border-t border-gray-200">
      {/* Home, Search, Profile, and Cart Icons */}
      <div className="flex justify-between w-full max-w-lg items-center">
        {/* Home Icon */}
        <div className="flex flex-col items-center cursor-pointer">
          <Icon icon="mdi:home" className="text-2xl text-gray-700 hover:text-black transition-all duration-200" />
          <span className="text-sm text-gray-600">Home</span>
        </div>

        {/* Search Icon */}
        <div className="flex flex-col items-center cursor-pointer">
          <Icon icon="mdi:magnify" className="text-2xl text-gray-700 hover:text-black transition-all duration-200" />
          <span className="text-sm text-gray-600">Search</span>
        </div>

        {/* Profile Icon */}
        <div className="flex flex-col items-center cursor-pointer">
          <Icon icon="mdi:account" className="text-2xl text-gray-700 hover:text-black transition-all duration-200" />
          <span className="text-sm text-gray-600">Profile</span>
        </div>

        {/* Cart Icon */}
        <div className="flex flex-col items-center cursor-pointer">
          <Icon
            icon="fluent:cart-24-regular"
            className="text-2xl text-gray-700 hover:text-black transition-all duration-200"
            onClick={() => alert('Navigating to Cart...')} // Replace with actual cart navigation
          />
          <span className="text-sm text-gray-600">Cart</span>
        </div>
      </div>
    </div>
  );
}

export default AddCart;
