import React, { useEffect, useState } from 'react';
import { Icon } from '@iconify/react/dist/iconify.js';
import { GetProductList } from '../Services/allApi';
import AddCart from './AddCart';

function Home() {
  const [items, setItems] = useState([]);

  const updateToCart = (id) => {
    setItems(items.map(item =>
      item.id === id ? { ...item, cartCount: 1 } : item
    ));
  };

  const plusItems = (id) => {
    setItems(items.map(item =>
      item.id === id ? { ...item, cartCount: item.cartCount + 1 } : item
    ));
  };

  const minusItems = (id) => {
    setItems(items.map(item =>
      item.id === id && item.cartCount > 1 ? { ...item, cartCount: item.cartCount - 1 } : item
    ));
  };

  const getItems = async () => {
    const username = "admin";
    const password = "admin123";
    const reqHeaders = {
      'Authorization': 'Basic ' + btoa(username + ":" + password)
    };
    const res = await GetProductList(reqHeaders);
    const newData = res?.data?.map((item) => ({
      id: item?.id,
      name: item?.itemName,
      description: item?.itemDescription,
      price: item?.price,
      image: item?.image ?? 'https://th.bing.com/th/id/OIP.XSCo5S6kP3o-7-jVqH4vGgHaE8?rs=1&pid=ImgDetMain',
      cartCount: 0,
    }));
    console.log(newData);
    setItems(newData);
  };

  useEffect(() => {
    getItems();
  }, []);

  return (
    <div className="flex justify-center bg-white min-h-screen w-full" style={{ fontFamily: 'ropa sans' }}>
      <div className="flex flex-col w-full pb-20">
        
        {/* Header Section */}
        <div className="bg-[#F39C12] py-4 shadow-lg">
          <div className="flex justify-between items-center px-4">
            <div className="text-white text-3xl font-bold">Aswin Restaurant</div>
            <div className="flex items-center gap-2">
              <Icon icon="akar-icons:user" className="text-white text-xl" />
              <Icon icon="ic:round-shopping-cart" className="text-white text-xl" />
            </div>
          </div>
        </div>

        {/* Search Section */}
        <div className="flex w-full justify-center px-3 mt-5">
          <div className="relative w-full max-w-xl">
            {/* Search Icon */}
            <Icon
              icon="icon-park-outline:search"
              className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400"
            />
            {/* Search Input */}
            <input
              className="w-full rounded-full py-3 pl-12 pr-4 border-2 border-[#F39C12] shadow-md bg-[#FFF1D1] text-gray-700 placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-[#F39C12] transition-all duration-300"
              type="text"
              placeholder="Search dishes or restaurants"
            />
          </div>
        </div>

        {/* Category Section */}
        <div className="flex flex-row px-3 items-center justify-between mt-4">
          <div className="flex gap-2 pt-3 font-bold items-center text-gray-600">
            <span className="text-gray-600 text-sm">Starters</span>
            <span><Icon icon="icon-park-solid:down-one" /></span>
          </div>
          <div className="flex flex-row gap-5">
            <div className="border border-green-500 rounded p-1">
              <div className="w-3 h-3 bg-green-500 rounded"></div>
            </div>
            <div className="border border-red-500 rounded p-1">
              <div className="w-3 h-3 bg-red-500 rounded"></div>
            </div>
          </div>
        </div>

        {/* Card Section */}
        <div className="flex flex-col gap-3 mt-3">
          {items?.map((item) => (
            <React.Fragment key={item.id}>
              <div className="w-full h-auto flex flex-col bg-white rounded-lg shadow-md overflow-hidden transform transition-all duration-300 ease-in-out hover:scale-105 hover:shadow-xl">
                {/* Card Content Container */}
                <div className="flex flex-row items-start p-3 gap-5">
                  {/* Image Section */}
                  <div className="w-36 h-36">
                    <img
                      src={item.image}
                      alt={item.name}
                      className="w-full h-full object-cover rounded-lg shadow-md transition-all duration-300 ease-in-out"
                    />
                  </div>

                  {/* Info Section */}
                  <div className="flex flex-col w-full">
                    {/* Item Name */}
                    <div className="flex justify-between items-center mb-2">
                      <h1 className="font-semibold text-lg text-gray-800 hover:text-[#F39C12] transition-colors duration-200">{item.name}</h1>
                    </div>

                    {/* Item Description */}
                    <div className="mb-2">
                      <p className="text-sm text-gray-600">
                        {item.description.slice(0, 60)} 
                        <span className="font-semibold text-[#F39C12] hover:underline">{item?.description.length > 60 ? '...more' : ''}</span>
                      </p>
                    </div>

                    {/* Price and Cart Controls */}
                    <div className="flex justify-between items-center mt-auto">
                      <h1 className="font-semibold text-xl text-[#F39C12]">₹ {item.price}</h1>

                      {item?.cartCount === 0 ? (
                        <button
                          onClick={() => updateToCart(item?.id)}
                          className="px-6 py-2 bg-[#F39C12] text-white rounded-full font-semibold shadow-lg hover:bg-[#e67e22] transition-all duration-300 transform hover:scale-105"
                        >
                          Add to Cart
                        </button>
                      ) : (
                        <div className="flex items-center gap-3 border-2 border-[#F39C12] rounded-full p-2">
                          <button
                            onClick={() => minusItems(item?.id)}
                            className="px-3 py-1 text-[#F39C12] font-semibold rounded-full hover:bg-[#F39C12] hover:text-white transition-all duration-300 transform hover:scale-110"
                          >
                            <Icon icon="ic:baseline-minus" />
                          </button>
                          <span className="font-semibold text-[#F39C12] text-lg">{item?.cartCount}</span>
                          <button
                            onClick={() => plusItems(item?.id)}
                            className="px-3 py-1 text-[#F39C12] font-semibold rounded-full hover:bg-[#F39C12] hover:text-white transition-all duration-300 transform hover:scale-110"
                          >
                            <Icon icon="ic:baseline-plus" />
                          </button>
                        </div>
                      )}
                    </div>
                  </div>
                </div>

                {/* Divider Line */}
                <div className="w-full h-[1px] bg-gray-300"></div>
              </div>
            </React.Fragment>
          ))}
        </div>
      </div>
      <AddCart className="shadow-2xl" />
    </div>
  );
}

export default Home;
