import { Icon } from '@iconify/react';

import React, { useEffect, useState, useRef } from 'react';
import { getMenuListByRestaurantIdAndRestaurantName, getRestaurantColorTheme } from '../Services/allApi';
import AddCart from './AddCart';
import { useSearchParams } from 'react-router';
import Slider from 'react-slick'; // For promotional carousel

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

function Home() {
  const [items, setItems] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('Recommended');
  const [selectedType, setSelectedType] = useState('All');
  const [ratingFilter, setRatingFilter] = useState(0);
  const [loading, setLoading] = useState(true);
  const [cartItems, setCartItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchParams] = useSearchParams();

  const restaurantId = searchParams.get('restaurantId');
  const restaurantName = searchParams.get('restaurantName') || 'Italian Dorado';

  const searchInputRef = useRef(null);
  const [themeColor, setThemeColor] = useState('#2ebf6c');
  const categories = ['Recommended', 'Bestsellers', 'Starters', 'Main Course', 'Desserts'];

  // Fetch theme color
  useEffect(() => {
    async function fetchColor() {
      const headers = { Authorization: 'Basic ' + btoa('admin:admin123') };
      const res = await getRestaurantColorTheme(restaurantId, headers);
      setThemeColor(res?.data?.backgroundColor || '#2ebf6c');
    }
    fetchColor();
  }, [restaurantId]);

  // Fetch items
  useEffect(() => {
    async function fetchItems() {
      const headers = { Authorization: 'Basic ' + btoa('admin:admin123') };
      const res = await getMenuListByRestaurantIdAndRestaurantName(restaurantId, restaurantName, headers);
      setItems(res?.data || []);
      setLoading(false);
    }
    fetchItems();
  }, [restaurantId, restaurantName]);

  // Filter Logic
  const filteredItems = items?.filter(item => {
    const name = item.name || ""; // Fallback to an empty string
    const description = item.description || ""; // Fallback to an empty string
    const category = item.category || ""; // Fallback to an empty string
  
    return (
      (selectedCategory === '' || category === selectedCategory) &&
      (selectedType === 'All' || item.type === selectedType) &&
      (
        name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        category.toLowerCase().includes(searchTerm.toLowerCase())
      )
    );
  });
  

  // Promotional Carousel Content
  const banners = [
    { id: 1, img: 'https://via.placeholder.com/300x150?text=Promo+1' },
    { id: 2, img: 'https://via.placeholder.com/300x150?text=Promo+2' },
    { id: 3, img: 'https://via.placeholder.com/300x150?text=Promo+3' }
  ];

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000
  };

  const updateToCart = (id) => {
    setItems((prevItems) =>
      prevItems.map((item) =>
        item.id === id ? { ...item, cartCount: (item.cartCount || 0) + 1 } : item
      )
    );
  
    const selectedItem = items.find((item) => item.id === id);
    if (selectedItem) {
      setCartItems((prevCart) => {
        const existingItem = prevCart.find((ci) => ci.id === id);
        if (!existingItem) {
          return [...prevCart, { ...selectedItem, cartCount: 1 }];
        }
        return prevCart.map((ci) =>
          ci.id === id ? { ...ci, cartCount: ci.cartCount + 1 } : ci
        );
      });
    }
  };
  

  return (
    <div className="bg-white min-h-screen font-sans text-gray-800">
      {/* Header */}
      <div className="px-4 py-4 bg-white sticky top-0 z-10 border-b">
        <h1 className="font-bold text-xl text-gray-800">Welcome to {restaurantName}</h1>
      </div>

      {/* Promotional Banner */}
      <div className="my-3 mx-4">
        <Slider {...settings}>
          {banners.map((banner) => (
            <div key={banner.id}>
              <img src={banner.img} alt="Promo" className="w-full rounded-lg shadow-md" />
            </div>
          ))}
        </Slider>
      </div>

      {/* Search */}
      <div className="p-4">
        <input
          ref={searchInputRef}
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search for dishes..."
          className="w-full border rounded-full px-4 py-2 text-sm focus:ring-2 focus:ring-green-500"
        />
      </div>

      {/* Filters */}
      <div className="flex items-center gap-3 px-4 mb-2">
        <div className="flex gap-2">
          {['Veg', 'Non-Veg', 'All'].map((type) => (
            <button
              key={type}
              onClick={() => setSelectedType(type)}
              className={`px-4 py-1 rounded-full text-sm ${
                selectedType === type ? 'bg-green-500 text-white' : 'bg-gray-100 text-gray-800'
              }`}
            >
              {type}
            </button>
          ))}
        </div>

        {/* Rating Filter */}
        <div className="ml-auto">
          <select
            value={ratingFilter}
            onChange={(e) => setRatingFilter(Number(e.target.value))}
            className="border rounded px-3 py-1 text-sm focus:ring-2 focus:ring-green-500"
          >
            <option value={0}>All Ratings</option>
            <option value={4}>4★ & Above</option>
            <option value={3}>3★ & Above</option>
          </select>
        </div>
      </div>

      {/* Category Navigation */}
      <div className="flex overflow-auto gap-4 px-4 py-2 bg-gray-100">
        {categories.map((category) => (
          <button
            key={category}
            onClick={() => setSelectedCategory(category)}
            className={`px-4 py-1 whitespace-nowrap rounded-full text-sm font-medium ${
              selectedCategory === category ? 'bg-green-500 text-white' : 'bg-gray-300 text-gray-800'
            }`}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Items */}
      <div>
        {loading ? (
          <div className="p-4">Loading...</div>
        ) : filteredItems.length === 0 ? (
          <div className="text-center p-10 text-gray-500">No items found</div>
        ) : (
          filteredItems.map((item) => (
            <div key={item.id} className="flex items-center gap-4 p-4 border-b">
              <div className="w-32 h-32 flex-shrink-0">
                <img
                  src={item.imageUrl}
                  alt={item.name}
                  className="w-full h-full object-cover rounded-lg shadow-sm"
                />
              </div>
              <div className="flex-1">
                <h2 className="font-bold text-lg">{item.name}</h2>
                <p className="text-sm text-gray-500">{item.description}</p>
                <div className="flex items-center gap-2 mt-1">
                  <span className="text-yellow-500 text-sm font-medium">{item.rating}★</span>
                  <span className="text-gray-600 text-sm">₹{item.price}</span>
                  {item.customisable && (
                    <span className="text-sm text-blue-500 font-semibold">Customisable</span>
                  )}
                </div>
              </div>
              <button
                className="bg-green-500 text-white px-4 py-2 rounded-full font-medium shadow-md"
                onClick={() => updateToCart(item.id)}
              >
                Add
              </button>
            </div>
          ))
        )}
      </div>

      {/* Cart */}
      <AddCart
        themeColor={themeColor}
        cartCount={cartItems.length}
        cartItems={cartItems}
        onSearchIconClick={() => searchInputRef.current?.focus()}
      />
    </div>
  );
}

export default Home;
