import React, { useState } from 'react';
import { Icon } from '@iconify/react'; // Import Iconify's Icon component

function AddCart() {
  const [clicked, setClicked] = useState(null); // Track which icon was clicked

  // Function to handle button click
  const handleClick = (button) => {
    setClicked(button);
    // Add your navigation or other actions here
    setTimeout(() => setClicked(null), 200); // Reset clicked state after a short delay
  };

  // A function to determine if the button is clicked and apply a darker color
  const getButtonClass = (button) => {
    return clicked === button
      ? 'text-[#e67e22]' // Darker orange when clicked
      : 'text-[#F39C12]'; // Default orange
  };

  return (
    <div className="fixed bottom-0 w-full h-20 bg-white drop-shadow-2xl z-50 flex items-center justify-around px-4 rounded-t-lg border-t border-gray-200">
      {/* Home, Search, Profile, and Cart Icons */}
      <div className="flex justify-between w-full max-w-lg items-center">
        
        {/* Home Icon */}
        <div
          className="flex flex-col items-center cursor-pointer p-3 transition-all duration-300 ease-in-out hover:scale-110"
          onClick={() => handleClick('home')}
        >
          <Icon
            icon="mdi:home"
            className={`text-3xl transition-all duration-300 ${getButtonClass('home')}`}
          />
          <span className={`text-sm mt-1 ${getButtonClass('home')}`}>Home</span>
        </div>

        {/* Search Icon */}
        <div
          className="flex flex-col items-center cursor-pointer p-3 transition-all duration-300 ease-in-out hover:scale-110"
          onClick={() => handleClick('search')}
        >
          <Icon
            icon="mdi:magnify"
            className={`text-3xl transition-all duration-300 ${getButtonClass('search')}`}
          />
          <span className={`text-sm mt-1 ${getButtonClass('search')}`}>Search</span>
        </div>

        {/* Profile Icon */}
        <div
          className="flex flex-col items-center cursor-pointer p-3 transition-all duration-300 ease-in-out hover:scale-110"
          onClick={() => handleClick('profile')}
        >
          <Icon
            icon="mdi:account"
            className={`text-3xl transition-all duration-300 ${getButtonClass('profile')}`}
          />
          <span className={`text-sm mt-1 ${getButtonClass('profile')}`}>Profile</span>
        </div>

        {/* Cart Icon */}
        <div
          className="flex flex-col items-center cursor-pointer p-3 transition-all duration-300 ease-in-out hover:scale-110"
          onClick={() => handleClick('cart')}
        >
          <Icon
            icon="fluent:cart-24-regular"
            className={`text-3xl transition-all duration-300 ${getButtonClass('cart')}`}
          />
          <span className={`text-sm mt-1 ${getButtonClass('cart')}`}>Cart</span>
        </div>
      </div>
    </div>
  );
}

export default AddCart;
